mapper.update:
UPDATE sysuser
SET LoginPwd = #{newPassword}
WHERE ID = #{id}

UPDATE sysuser
SET UserName = #{userName}, UserDepartment = #{userDepartment}, UserPhoneNum = #{userPhoneNum}
WHERE loginID = #{loginID}

UPDATE sysuser
SET LoginPwd = #{systemUserDefaultPassword}
WHERE LoginID = #{loginID}

mapper.select:
SELECT COUNT(*) > 0
FROM sysuser
WHERE LoginID = #{loginID}

SELECT LoginPwd
FROM sysuser
WHERE LoginID = #{loginID}

SELECT ID, LoginID, UserName, UserRoleName, UserDepartment, UserPhoneNum
FROM sysuser
WHERE LoginID = #{loginID}

SELECT ID, LoginID, UserName, UserRoleName, UserDepartment, UserPhoneNum
FROM sysuser
WHERE ID = #{id}

SELECT COUNT(*) > 0
FROM sysuser
WHERE ID = #{id} AND LoginPwd = #{oldPassword}
